import pytest


@pytest.mark.vcr()
class TestTeam:
    def test_team(self, pycelonis_celonis):
        permissions = pycelonis_celonis.team.get_permissions()

        assert permissions.find("event-collection", search_attribute="service_name")
        assert permissions.find("package-manager", search_attribute="service_name")

        team = pycelonis_celonis.team.get_team()
        assert team.name
