from unittest.mock import MagicMock

from pycelonis.celonis import Celonis
from pycelonis.ems.data_integration import DataIntegration
from pycelonis.ems.studio import Studio
from pycelonis.ems.team import Team


class TestCelonis:
    def test_celonis_object_has_attributes_set(self):
        client = MagicMock()
        celonis = Celonis(client)

        assert celonis.client == client
        assert isinstance(celonis.data_integration, DataIntegration)
        assert isinstance(celonis.studio, Studio)
        assert isinstance(celonis.team, Team)
